import React, { useState }  from 'react'

const CreateAdmin = () => {
    const [formData, setFormData] = useState({
        name: '',
        title: '',
        description: '',
      });
    
      const [generatedCards, setGeneratedCards] = useState([]);
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };
    
      const generateCard = () => {
        const { name, title, description } = formData;
    
        // Create a new card object
        const newCard = {
          id: Date.now(),
          name,
          title,
          description,
        };
    
        // Update the state with the new card
        setGeneratedCards((prevCards) => [...prevCards, newCard]);
    
        // Clear the form data for the next input
        setFormData({
          name: '',
          title: '',
          description: '',
        });
      };
  return (
    <>
 <div>
      <form>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />

        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="4"
          required
        ></textarea>

        <button type="button" onClick={generateCard}>
          Generate Card
        </button>
      </form>

      <div>
        {generatedCards.map((card) => (
          <div key={card.id} className="card">
            <h2>{card.name}</h2>
            <h3>{card.title}</h3>
            <p>{card.description}</p>
          </div>
        ))}
      </div>
    </div>

    
    </>
  )
}

export default CreateAdmin
