import React, { useState }  from 'react'

const CreateAdmin = () => {
    const [formData, setFormData] = useState({
        name: '',
        title: '',
        description: '',
      });
    
      const [generatedCard, setGeneratedCard] = useState(null);
    
      const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value,
        });
      };
    
      const generateCard = () => {
        const { name, title, description } = formData;
    
        // Create a card JSX
        const cardJSX = (
          <div className="card">
            <h2>{name}</h2>
            <h3>{title}</h3>
            <p>{description}</p>
          </div>
        );
    
        // Set the generated card in the state
        setGeneratedCard(cardJSX);
      };
  return (
    <>

<div>
      <form>
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          id="name"
          name="name"
          value={formData.name}
          onChange={handleChange}
          required
        />

        <label htmlFor="title">Title:</label>
        <input
          type="text"
          id="title"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />

        <label htmlFor="description">Description:</label>
        <textarea
          id="description"
          name="description"
          value={formData.description}
          onChange={handleChange}
          rows="4"
          required
        ></textarea>

        <button type="button" onClick={generateCard}>
          Generate Card
        </button>
      </form>

      <div id="cardContainer">{generatedCard}</div>
    </div>

    
    </>
  )
}

export default CreateAdmin
