import { useEffect, useState } from 'react';
import {db} from '../firebase'
import {getDocs, collection} from 'firebase/firestore';
import man from '../images/man.png'
import line from '../images/line.png'
import Header from '../header/Header'
import profile from '../images/profile.png'
import adminVerified from '../images/adminVerified.png'
import SuperAdmin from '../reuseable components/SuperAdmin'
import UserCard from '../reuseable components/UserCard'

const BlockUser = () => {

   //for firebase setup
   const [userData, setUserData]=useState([]);
   const [selectedCardData, setSelectedCardData] = useState(null);
   const movieCollectionRef = collection(db,"users");
 
   const handleCardClick = (users) => {
     setSelectedCardData(users);
   };
 
   useEffect(()=>{
     const getUserData = async()=>{
       try{
         const data= await getDocs(movieCollectionRef);
         const filteredData = data.docs.map((doc)=>({
           ...doc.data(),
           id:doc.id,
         }));
         console.log(userData);
         setUserData(filteredData);
       }catch(err){
         console.error(err);
       }
     }
     getUserData();
 
   }, []);
 
  const data={
    profile: profile,
    name:"James",
    id:"0000 0000 0000",
    contact:"+92 399 3939838",
    mail:"james@gmail.com",
    Linkname1:"Unblock",
    Linkname2:"Delete",
    
    title:"Basic",
  }

  const SuperAdminData = {
    profile:profile,
    Verified: "Verified",
    verifiedLogo:adminVerified,
    name:"james",
    id:"0000 0000 0000",
    email:"james@gmail.com",
    contact:"+92307662172",
    religion:"non-muslim",
    country:"America",
    city:"city",
    study:"Masters",
    job:"Engineer",
    workplace:"company",
    dob:"1 Srpt,2000",
    gender:"male",
    height:"5.9 FT",
  }
  
  return (
    <>
    <div className=" d-flex" >
    <div style={{width:"75%",borderRadius:"10px" ,height:"735px",backgroundColor:"#161617"}}>

        <div className="row">
          <Header/>
          <h2 className='header-title ms-3'>Blocked User</h2>
          <div className="row" style={{marginLeft:"1px"}}>

          {userData.map((users)=>(
         <UserCard firstName={users.firstName} phone={users.phone} id={users.id} email={users.email}  image={users.profileimage} {...propsCardData} onClick={() => handleCardClick(users)} />
  ))}
            </div>
          <p className='showing'>Showing Result 8 of 15</p>

           
        </div>


    </div>


    <div className='mx-auto' style={{width:"24%",borderRadius:"10px",height:"735px",backgroundColor:"#161617"}}>
          <div className='d-flex mt-2 justify-content-center'>
            <img src={man} alt="" />
            <h4 className='superadmintitle d-flex align-items-center ms-3'>Super Admin</h4>
          </div>
          <img src={line} className='mt-2' width={"100%"} alt="" />
          
          <SuperAdmin selectedCardData={selectedCardData}  />
          <div className='sidebar-btn flex flex-column text-center'>
            <button className='w-75 py-2'>Unblock</button>
          </div>
               

    </div>
    </div>
    </>
  )
}

export default BlockUser
