import React,{useState,useEffect} from 'react'
import man from '../images/man.png'
import line from '../images/line.png'
import Header from '../header/Header'
import SuperAdmin from '../reuseable components/SuperAdmin'
import UserCard from '../reuseable components/UserCard'
import {db} from '../firebase'
import {getDocs, collection} from 'firebase/firestore';
import firebase from 'firebase/app';
import 'firebase/database';

const UserVerifications = () => {

    //for firebase setup
    const [userData, setUserData]=useState([]);
    const [selectedCardData, setSelectedCardData] = useState(null);
    const CollectionRef = collection(db,"users");
  
    const handleCardClick = (users) => {
      setSelectedCardData(users);
    };
  
    useEffect(()=>{
      const getUserData = async()=>{
        try{
          const data= await getDocs(CollectionRef);
          const filteredData = data.docs.map((doc)=>({
            ...doc.data(),
            id:doc.id,
          }));
          console.log(userData);
          setUserData(filteredData);
        }catch(err){
          console.error(err);
        }
      }
      getUserData();
  
    }, []);

  const headerDropdown = {
    headerLink1:"All",
    headerLink2:"Basics",
    headerLink3:"Gold",
    headerLink4:"Vip",
  }


  const handleVerify = () => {
    if (userData) {
      // Update isVerified status in Firebase
      const movieCollectionRef = firebase.database().movieCollectionRef(`users/${userData.id}/isVerified`);
      movieCollectionRef.set('verified')
        .then(() => {
          console.log('Verification status updated successfully');
          setUserData(null); // Clear the selected user after verification
        })
        .catch((error) => {
          console.error('Error updating verification status:', error);
        });
    }
  };

  return (
    <>
     <div className=" d-flex" style={{height:"100%"}} >
    <div  style={{width:"75%",borderRadius:"10px",backgroundColor:"#161617"}}>
        <div className="row">
          <Header {...headerDropdown}/>
          <h2 className='header-title ms-3'>User Verifications</h2>
          <div className="row" style={{marginLeft:"1px"}}>

          {userData.map((users)=>(
                <UserCard firstName={users.firstName} phone={users.phone} id={users.id} email={users.email} active={users.isVerified==="verified"?"Verified":"pending"} title={"Basic"}   dropdowndisplay1={{display:"none"}}  image={users.profileimage} onClick={() => handleCardClick(users)} />
                ))}

          </div>
          <p className='showing'>Showing Result 8 of 15</p> 
        </div>
    </div>

    <div className='mx-auto' style={{width:"24%",borderRadius:"10px",backgroundColor:"#161617"}}>
          <div className='d-flex mt-2 justify-content-center'>
            <img src={man} alt="" />
            <h4 className='superadmintitle d-flex align-items-center ms-3'>Super Admin</h4>
          </div>
          <img src={line} className='mt-2' width={"100%"} alt="" />

          <SuperAdmin selectedCardData={selectedCardData} />

          <div className='sidebar-btn flex flex-column text-center align-items-end'>
            <a href="" ><button className='w-75 py-2' onClick={handleVerify}>Verify</button></a>
            <a href=""><button className='w-75 py-2 mt-2 ' >Reject</button></a>
          </div>
        
    </div>
    </div>

    </>
  )
}

export default UserVerifications
